mapper.select:
SELECT A.ACTIVATE_ID, A.USER_ID, A.ACTIVATE_DATE, U.NAME, U.PHONE_NUMBER, A.STORE_ID FROM ACTIVATE A JOIN GOOD_PRICE_BUSINESS GPB ON A.STORE_ID = GPB.STORE_ID JOIN USER_TABLE U ON A.USER_ID = U.USER_ID JOIN BUSINESS_USER BU ON GPB.USER_ID = BU.USER_ID WHERE GPB.USER_ID = #{userId}
AND A.ACTIVATE_DATE >= TRUNC(SYSDATE) ORDER BY A.ACTIVATE_DATE ASC
AND A.ACTIVATE_DATE < TRUNC(SYSDATE) ORDER BY A.ACTIVATE_DATE DESC

mapper.update:
        UPDATE APPROVAL_MANAGEMENT
		SET FINAL_APPROVAL_STATUS = #{finalApprovalStatus}
		WHERE STORE_ID = #{storeId}
