mapper.sql:
SELECT tb.*,
ROW_NUMBER() OVER (ORDER BY like_count DESC, store_name) AS row_num
ROW_NUMBER() OVER (ORDER BY review_avg DESC, store_name) AS row_num
FROM ( SELECT gpb.store_id, gpb.user_id, gpb.INDUSTRY_ID, gpb.CONTACT, gpb.STORE_IMAGE, gpb.TAKEOUT, gpb.DELIVERY, gpb.WIFI, gpb.PET, gpb.KID, gpb.PARKING, bu.STORE_NAME, bu.ADDRESS, bu.LAT, bu.LNG, i.INDUSTRY_NAME, sgg.SIGUNGU_NAME, sgg.SIGUNGU_ID , sd.SIDO_NAME, am.price, am.main_menu, (SELECT NVL(SUM(LIKE_COUNT), 0) FROM review WHERE store_id = gpb.store_id ) AS like_count, (SELECT count(*) FROM REVIEW r WHERE r.STORE_ID = GPB.STORE_ID ) AS review_count, (SELECT TRUNC(AVG(rating),1) FROM REVIEW r WHERE r.STORE_ID = GPB.STORE_ID ) AS review_avg, (SELECT COUNT(*) FROM review r WHERE r.STORE_ID = gpb.STORE_ID AND r.RATING = 5) AS rating_5_count, (SELECT COUNT(*) FROM review r WHERE r.STORE_ID = gpb.STORE_ID AND r.RATING = 4) AS rating_4_count, (SELECT COUNT(*) FROM review r WHERE r.STORE_ID = gpb.STORE_ID AND r.RATING = 3) AS rating_3_count, (SELECT COUNT(*) FROM review r WHERE r.STORE_ID = gpb.STORE_ID AND r.RATING = 2) AS rating_2_count, (SELECT COUNT(*) FROM review r WHERE r.STORE_ID = gpb.STORE_ID AND r.RATING = 1) AS rating_1_count, ROW_NUMBER() OVER (ORDER BY CASE WHEN gpb.created_at >= SYSDATE - 7 THEN 1 ELSE 2 END, gpb.created_at DESC ) AS new_store FROM user_table ut INNER JOIN BUSINESS_USER bu ON ut.USER_ID = bu.USER_ID INNER JOIN SIGUNGU sgg ON bu.SIGUNGU_ID = sgg.SIGUNGU_ID INNER JOIN SIDO sd ON sgg.SIDO_ID = sd.SIDO_ID INNER JOIN GOOD_PRICE_BUSINESS gpb ON ut.USER_ID = gpb.USER_ID INNER JOIN INDUSTRY i ON gpb.INDUSTRY_ID = i.INDUSTRY_ID INNER JOIN APPROVAL_MANAGEMENT am ON gpb.store_id = am.store_id
AND sd.SIDO_ID = #{data.sidoId}
AND sd.SIDO_NAME = #{data.sidoName}
AND sgg.SIGUNGU_ID = #{data.sigunguId}
AND sgg.SIGUNGU_NAME = #{data.sigunguName}
AND i.INDUSTRY_ID = #{data.industryId}
AND i.INDUSTRY_NAME = #{data.industryName}
AND bu.STORE_NAME LIKE '%' || #{data.storeName} || '%'
AND am.MAIN_MENU LIKE '%' || #{data.mainMenu} || '%'
AND gpb.TAKEOUT = #{data.takeout}
AND gpb.DELIVERY = #{data.delivery}
AND gpb.WIFI = #{data.wifi}
AND gpb.PET = #{data.pet}
AND gpb.KID = #{data.kid}
AND gpb.PARKING = #{data.parking}
) tb
